// enums
export enum SaleType { FERIA, DIRECT, CONSIGNMENT, BUNDLE_SALE, ONLINE, NFT, GIFT, EXCHANGE }
export enum SaleStatus { PENDING, POSTED, DONE, CANCELLED }
export enum PaymentMethod { CASH, CARD, TRANSFER, BTC, CRC, OTHER }

// base
type Discount = { amount?: number; percent?: number }; // either-or; validated
type Payment = { method: PaymentMethod; amount: number; currency: Currency; receivedAt?: Date };

// lines
type SaleLineBase = {
  lineId: string;
  kind: 'item' | 'bundle' | 'service';
  description?: string;
  taxAmount?: number;
  discount?: Discount;
};

type ItemSaleLine = SaleLineBase &amp; {
  kind: 'item';
  itemId: string;           // precise item
  quantity: number;
  unitPrice: number;
};

type BundleSaleLine = SaleLineBase &amp; {
  kind: 'bundle';
  itemType: ItemType;       // e.g., STICKER_BUNDLE
  subItemType?: SubItemType;
  siteId: string;
  quantity: number;
  unitPrice: number;
};

type ServiceLine = SaleLineBase &amp; {
  kind: 'service';
  station: Station;         // where service belongs (Design/Production/etc.)
  category: Category;
  revenue: number;          // direct service revenue
  createTask?: boolean;     // optional basic task creation
};

type SaleLine = ItemSaleLine | BundleSaleLine | ServiceLine;

// sale
export interface Sale extends BaseEntity {
  saleDate: Date;                 // business date of the sale
  type: SaleType;
  status: SaleStatus;
  siteId: string;
  counterpartyName?: string;
  isNotPaid?: boolean;
  isNotCharged?: boolean;

  overallDiscount?: Discount;
  lines: SaleLine[];
  payments?: Payment[];

  // totals (computed, persisted for fast reads)
  totals: {
    subtotal: number;
    discountTotal: number;
    taxTotal: number;
    totalRevenue: number;
  };

  // ties to tasks (not statuses)
  requiresReconciliation?: boolean;
  reconciliationTaskId?: string;
  requiresRestock?: boolean;
  restockTaskId?: string;

  // entity creation
  createdTaskId?: string;         // when a sale spawns a task (e.g., mural)
}
